import React, { useState, useEffect } from 'react';

function EditContact({ contactId, onClose }) {
  const [name, setName] = useState('');
  const [phoneno, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');

  // API endpoint for editing a contact (replace with your actual endpoint)
  const editApiUrl = `http://localhost:8080/update/${contactId}`;

  // Fetch contact data and populate form fields when the component mounts
  useEffect(() => {
    fetch(editApiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        // Populate form fields with fetched data
        setName(data.name);
        setPhone(data.phoneno);
        setEmail(data.email);
        setAddress(data.address);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [editApiUrl]);

  const handleEdit = () => {
    const updatedContact = {
      name,
      phoneno,
      email,
      address,
    };

    // Send a PUT request to update the contact
    fetch(editApiUrl, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedContact),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        console.log('Contact updated successfully');
        // Close the edit form upon successful update
        onClose();
      })
      .catch((error) => {
        console.error('Error updating contact:', error);
      });
  };

  const handleCancel = () => {
    // Close the edit form without saving changes
    onClose();
  };

  return (
    <div>
      {isOpen && (
        <form>
          <div>
            <label>Name:</label>
            <input type="text" value={name} onChange={handleNameChange} />
          </div>
          <div>
            <label>Phone Number:</label>
            <input type="text" value={phoneno} onChange={handlePhoneChange} />
          </div>
          <div>
            <label>Email:</label>
            <input type="email" value={email} onChange={handleEmailChange} />
          </div>
          <div>
            <label>Address:</label>
            <textarea value={address} onChange={handleAddressChange} />
          </div>
          <div>
            <button type="button" onClick={handleEdit}>
              Save Changes
            </button>
            <button type="button" onClick={handleCancel}>
              Cancel
            </button>
          </div>
        </form>
      )}
    </div>
  );
}

export default EditContact;
