import React, { useState, useEffect } from 'react';
import { Model } from 'react-model'; // Assuming 'react-model' is correctly installed and imported
import CreateContact from './CreateContact';
import EditContact from './EditContact';

function UpdateContact() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [editingItemId, setEditingItemId] = useState(null);
  const [visible, setVisible] = useState(false);

  const apiUrl = 'http://localhost:8080/'; // Replace with your API endpoint URL

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    const filtered = data.filter((item) =>
      item.Name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredData(filtered);
  }, [data, searchTerm]);

  const fetchData = () => {
    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((jsonData) => {
        setData(jsonData);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };

  const handleDelete = (Id) => {
    const deleteApiUrl = `http://localhost:8080/delete-id/${Id}`;
    fetch(deleteApiUrl, {
      method: 'DELETE',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const updatedData = data.filter((item) => item.Id !== Id);
        setData(updatedData);
      })
      .catch((error) => {
        console.error('Error deleting data:', error);
      });
  };

  const handleEditClick = (Id) => {
    setVisible(true);
    setEditingItemId(Id);
  };

  const handleEditComplete = () => {
    setVisible(false); // Close the modal when editing is complete
    setEditingItemId(null);
  };

  return (
    <div>
      <h1>AddressBook</h1>
      <div className="container">
        <div className="search-container">
          <input
            className="search-bar"
            type="text"
            placeholder="Search by Name"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="create-container">
          <CreateContact />
        </div>
      </div>
      {searchTerm === '' ? (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Mobile No</th>
              <th>Email</th>
              <th>Address</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item) => (
              <tr key={item.Id}>
                <td>{item.Name}</td>
                <td>{item.Mobileno}</td>
                <td>{item.Email}</td>
                <td>{item.Address}</td>
                <td>
                  <button onClick={() => handleEditClick(item.Id)}>Edit</button>
                  <button onClick={() => handleDelete(item.Id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : filteredData.length > 0 ? (
        <ul>
          {filteredData.map((item) => (
            <li key={item.Id}>
              <p>ID: {item.Id}</p>
              <p>Name: {item.Name}</p>
              <p>Email: {item.Email}</p>
              <p>Mobile No: {item.Mobileno}</p>
              <p>Address: {item.Address}</p>
              <button onClick={() => handleEditClick(item.Id)}>Edit</button>
              <button onClick={() => handleDelete(item.Id)}>Delete</button>
            </li>
          ))}
        </ul>
      ) : null}
      <Model isOpen={visible}>
        {editingItemId !== null && (
          <EditContact contactId={editingItemId} onEditComplete={handleEditComplete} />
        )}
      </Model>
    </div>
  );
}

export default UpdateContact;
