import React, { useState, useEffect } from 'react';

function UpdateContact() {
    const [data, setData] = useState([]);
    const apiUrl = 'http://localhost:8080/'; // Replace with your API endpoint URL

    useEffect(() => {
        // Fetch data from the API endpoint when the component mounts
        fetchData();
    }, []);

    const fetchData = () => {
        // Fetch data from the API endpoint
        fetch(apiUrl)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((jsonData) => {
                setData(jsonData); // Update the state with the fetched data
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };

    const handleDelete = (name) => {
        const deleteApiUrl = `http://localhost:8080/delete-user/${name}`; 

        // Send a DELETE request to the delete endpoint
        fetch(deleteApiUrl, {
            method: 'DELETE',
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                // Remove the deleted item from the data in state
                const updatedData = data.filter((item) => item.Name !== name);
                setData(updatedData);
            })
            .catch((error) => {
                console.error('Error deleting data:', error);
            });
    };

    const handleEdit = (id) => {
        const deleteApiUrl = `http://localhost:8080/update-id/${id}`; 

        // Send a DELETE request to the delete endpoint
        fetch(deleteApiUrl, {
            method: 'DELETE',
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                // Remove the deleted item from the data in state
                const updatedData = data.filter((item) => item.Id !== id);
                setData(updatedData);
            })
            .catch((error) => {
                console.error('Error deleting data:', error);
            });
    };

    return (
        <div>
            <h1>AddressBook</h1>
            {data.length > 0 ? (
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Mobile No</th>
                        <th>Email</th>
                        <th>Address</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item) => (
                            <tr key={item.Id}>
                            <td>{item.Name}</td>
                            <td>{item.Mobileno}</td>
                            <td>{item.Email}</td>
                            <td>{item.Address}</td>
                            <td><button onClick={() => handleEdit(item.Id)}>Edit</button></td>
                            <td><button onClick={() => handleDelete(item.Name)}>Delete</button></td>
                            </tr>
                    ))}
                </tbody>
            </table>
            ) :null
            }
        </div>
    );
}

export default UpdateContact;
