import React, { useState, useEffect } from 'react';
import { Model } from 'react-model';

function EditContact({ contactId, onClose }) {
  const [name, setName] = useState('');
  const [mobileno, setMobileno] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [visible, setVisible] = useState(true);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const apiUrl = `http://localhost:8080/update/${contactId}`;

  useEffect(() => {
    // Fetch contact data and populate form fields when the component mounts
    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setName(data.name);
        setMobileno(data.mobileno);
        setEmail(data.email);
        setAddress(data.address);
        setIsLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setIsLoading(false);
      });
  }, [apiUrl]);

  const handleNameChange = (e) => setName(e.target.value);
  const handleMobilenoChange = (e) => setMobileno(e.target.value);
  const handleEmailChange = (e) => setEmail(e.target.value);
  const handleAddressChange = (e) => setAddress(e.target.value);

  const handleEdit = () => {
    const updatedContact = {
      name,
      mobileno,
      email,
      address,
    };

    // Send a PUT request to update the contact
    fetch(apiUrl, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedContact),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        console.log('Contact updated successfully');
        setVisible(false); // Close the modal after a successful update
        onClose(); // Notify the parent component of the edit completion
      })
      .catch((error) => {
        setError(error.message);
      });
  };

  const handleCancel = () => {
    onClose();
  };

  return (
    <div>
      {visible && (
            <form>
              <div className="form-group">
                <label>Name:</label>
                <input type="text" value={name} onChange={handleNameChange} required />
              </div>
              <div className="form-group">
                <label>Phone Number:</label>
                <input type="text" value={mobileno} onChange={handleMobilenoChange} required />
              </div>
              <div className="form-group">
                <label>Email:</label>
                <input type="email" value={email} onChange={handleEmailChange} required />
              </div>
              <div className="form-group">
                <label>Address:</label>
                <textarea value={address} onChange={handleAddressChange} required />
              </div>
              <div className="form-group">
                <button type="button" onClick={handleEdit}>
                  Save Changes
                </button>
                <button type="button" onClick={handleCancel}>
                  Cancel
                </button>
              </div>
            </form>
          )}
    </div>
  );
}

export default EditContact;
