import React, { useState, useEffect } from 'react';

function EditContact({ contactId }) {
  const [name, setName] = useState('');
  const [phoneno, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [isOpen, setIsOpen] = useState(true); // Initially open the form


  const handleCancel = () => {
    // Handle the cancel action (e.g., reset form fields) if needed
    // Close the form by setting isOpen to false
    setIsOpen(false);
  };

  // API endpoint for editing a contact (replace with your actual endpoint)
  const editApiUrl = `http://localhost:8080/update/${contactId}`;

  // Fetch contact data and populate form fields when the component mounts
  useEffect(() => {
    fetch(editApiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        // Populate form fields with fetched data
        setName(data.name);
        setPhone(data.phoneno);
        setEmail(data.email);
        setAddress(data.address);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [editApiUrl]); // Use the contactId in the URL to trigger the fetch when it changes

  const handleEdit = () => {
    const updatedContact = {
      name,
      phoneno,
      email,
      address,
    };

    // Send a PUT request to update the contact
    fetch(editApiUrl, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedContact),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        console.log('Contact updated successfully');
      })
      .catch((error) => {
        console.error('Error updating contact:', error);
      });
  };

  return (
    <div>
      <form>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>Phone Number:</label>
          <input type="text" value={phoneno} onChange={(e) => setPhone(e.target.value)} />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div>
          <label>Address:</label>
          <textarea value={address} onChange={(e) => setAddress(e.target.value)} />
        </div>
        <div>
          <button type="button" onClick={handleEdit}>Save Changes</button>
        </div>
        <div>
        <button type="button" onClick={handleCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
}

export default EditContact;
