import React, { useState, useEffect } from 'react';
import CreateContact from './CreateContact';
import './index.css'
import EditContact from './EditContact';

function UpdateContact() {
    const [data, setData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [showComponent, setShowComponent] = useState(false);
    const [editItemId, setEditItemId] = useState(null);

    const apiUrl = 'http://localhost:8080/'; // Replace with your API endpoint URL

    const handleEditClick = (itemId) => {
        setEditItemId(itemId);
      };
    
      const handleSave = (itemId, newName) => {
        // Update the data with the edited name
        const updatedData = data.map((item) =>
          item.id === itemId ? { ...item, name: newName } : item
        );
        setData(updatedData);
    
        // Clear the editItemId to exit edit mode
        setEditItemId(null);
      };

    const handleClick = () => {
        setShowComponent(true);
      };

    useEffect(() => {
        // Fetch data from the API endpoint when the component mounts
        fetchData();
    }, []);

    useEffect(() => {
        // Filter data based on the searchTerm
        const filtered = data.filter((item) =>
            item.Name.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setFilteredData(filtered);
    }, [data, searchTerm]);


    const fetchData = () => {
        // Fetch data from the API endpoint
        fetch(apiUrl)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((jsonData) => {
                setData(jsonData); // Update the state with the fetched data
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };

    const handleDelete = (name) => {
        const deleteApiUrl = `http://localhost:8080/delete-name/${name}`; 

        // Send a DELETE request to the delete endpoint
        fetch(deleteApiUrl, {
            method: 'DELETE',
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                // Remove the deleted item from the data in state
                const updatedData = data.filter((item) => item.Name !== name);
                setData(updatedData);
            })
            .catch((error) => {
                console.error('Error deleting data:', error);
            });
    };


    return (
        <div>
            <h1>AddressBook</h1>
            <div className="container">

           <div className="search-container"> <input                   
               className="search-bar"
                type="text"
                placeholder="Search by Name"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            </div>
            <div className="create-container">  
                <CreateContact />
                </div>
            </div>
             {searchTerm === '' ? (
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Mobile No</th>
                        <th>Email</th>
                        <th>Address</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item) => (
                            <tr key={item.Id}>
                            <td>{item.Name}</td>
                            <td>{item.Mobileno}</td>
                            <td>{item.Email}</td>
                            <td>{item.Address}</td>
                            <button onClick={handleClick}>Edit</button>
                            {showComponent && <EditContact />}
                            <td><button onClick={() => handleDelete(item.Name)}>Delete</button></td>
                            </tr>
                    ))}
                </tbody>
            </table>
            ):(
                filteredData.length > 0 ? (
                    <ul>
                        {filteredData.map((item) => (
                            <li key={item.Id}>
                                <p>ID: {item.Id}</p>
                                <p>Name: {item.Name}</p>
                                <p>Email: {item.Email}</p>
                                <p>Mobile No: {item.Mobileno}</p>
                                <p>Address: {item.Address}</p>
                                <button onClick={handleClick}>Edit</button>
                                {showComponent && <EditContact />}
                                <button onClick={() => handleDelete(item.Id)}>Delete</button>
                            </li>
                        ))}
                    </ul>
                ) : null
            )}
        </div>
    );
}

export default UpdateContact;
