import React, { useState, useEffect } from 'react';
import CreateContact from './CreateContact';
import EditContact from './EditContact';

function UpdateContact() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [editingItemId, setEditingItemId] = useState(null); // Track the ID of the item being edited
  const [selectedFile, setSelectedFile] = useState(null); // State for selected file

  const apiUrl = 'http://localhost:8080/'; // Replace with your API endpoint URL

  useEffect(() => {
    // Fetch data from the API endpoint when the component mounts
    fetchData();
  }, []);

  useEffect(() => {
    // Filter data based on the searchTerm
    const filtered = data.filter((item) =>
      item.Name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredData(filtered);
  }, [data, searchTerm]);

  const fetchData = () => {
    // Fetch data from the API endpoint
    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((jsonData) => {
        setData(jsonData); // Update the state with the fetched data
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };

  const handleDelete = (Id) => {
    const deleteApiUrl = `http://localhost:8080/delete-id/${Id}`;

    // Send a DELETE request to the delete endpoint
    fetch(deleteApiUrl, {
      method: 'DELETE',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        // Remove the deleted item from the data in state
        const updatedData = data.filter((item) => item.Id !== Id);
        setData(updatedData);
      })
      .catch((error) => {
        console.error('Error deleting data:', error);
      });
  };

  const handleEditClick = (Id) => {
    // Set the ID of the item being edited
    setEditingItemId(Id);
  };

  const handleEditComplete = () => {
    // Clear the editing state
    setEditingItemId(null);
  };

  const handleFileChange = (e) => {
    // Handle file selection
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  return (
    <div>
      <h1>AddressBook</h1>
      <div className="container">
        <div className="search-container">
          <input
            className="search-bar"
            type="text"
            placeholder="Search by Name"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="create-container">
          <CreateContact />
        </div>
      </div>
      {searchTerm === '' ? (
        <table>
          <thead>
            <tr>
              <th>Profile</th>
              <th>Name</th>
              <th>Mobile No</th>
              <th>Email</th>
              <th>Address</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item) => (
              <tr key={item.Id}>
                <td><input
            type="file"
            accept=".jpg, .jpeg, .png"
            onChange={handleFileChange}
          /></td>
                <td>{item.Name}</td>
                <td>{item.Mobileno}</td>
                <td>{item.Email}</td>
                <td>{item.Address}</td>
                <td>
                  <button onClick={() => handleEditClick(item.Id)}>Edit</button>
                  {editingItemId === item.Id && (
                    <EditContact
                      contact={item}
                      onEditComplete={handleEditComplete}
                    />
                  )}
                </td>
                <td>
                  <button onClick={() => handleDelete(item.Id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        filteredData.length > 0 ? (
          <ul>
            {filteredData.map((item) => (
              <li key={item.Id}>
              <p>Profile<input
            type="file"
            accept=".jpg, .jpeg, .png"
            onChange={handleFileChange}
          /></p>  
                <p>ID: {item.Id}</p>
                <p>Name: {item.Name}</p>
                <p>Email: {item.Email}</p>
                <p>Mobile No: {item.Mobileno}</p>
                <p>Address: {item.Address}</p>
                <button onClick={() => handleEditClick(item.Id)}>Edit</button>
                {editingItemId === item.Id && (
                  <EditContact
                    contact={item}
                    onEditComplete={handleEditComplete}
                  />
                )}
                <button onClick={() => handleDelete(item.Id)}>Delete</button>
              </li>
            ))}
          </ul>
        ) : null
      )}
    </div>
  );
}

export default UpdateContact;
