import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import CreateContactForm from './CreateContactForm';


function CreateContact() {
    const [data, setData] = useState(null);
    const [isFormVisible, setFormVisible] = useState(false);
    const [contacts, setContacts] = useState([]); // Store contacts data
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [mobileNo, setMobileNo] = useState('');
    const [address, setAddress] = useState('');
    const apiUrl = 'http://localhost:8080/add-user/'; // Replace with your API endpoint URL


    const openForm = () => {
        setFormVisible(true);
    };

    const closeForm = () => {
        setFormVisible(false);
    };

    const addContact = (newContact) => {
        // Add the new contact to the list of contacts
        setContacts([...contacts, newContact]);
    };


    useEffect(() => {
        fetch(apiUrl)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((jsonData) => {
                setData(jsonData);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, []);

    return (
        <div className="app">
            <button className='create-contact-button' onClick={openForm}> 
            <FontAwesomeIcon icon={faPlus} />
            Create Contact</button>

            {isFormVisible && (
                <CreateContactForm onClose={closeForm} onSubmit={addContact} />
            )}

<div className="form-modal">
            <h2>Create Contact</h2>
            <form>
                <div className="form-group">
                    <label>Name:</label>
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Email:</label>
                    <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Mobile No:</label>
                    <input type="text" value={mobileNo} onChange={(e) => setMobileNo(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Address:</label>
                    <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
                </div>
                <div className="buttons">
                    <button type="button" onClick={onClose}>Cancel</button>
                    <button type="button" onClick={handleSubmit}>Create</button>
                </div>
            </form>
        </div>
        </div>
    );
