import React, { useState, useEffect } from 'react';
import CreateContact from './CreateContact';
import './index.css';
import EditContact from './EditContact';

function UpdateContact() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [editingItemId, setEditingItemId] = useState(null); // Track the ID of the item being edited
  const [editContactData, setEditContactData] = useState(null); // Store contact data for editing

  const apiUrl = 'http://localhost:8080/'; // Replace with your API endpoint URL

  useEffect(() => {
    // Fetch data from the API endpoint when the component mounts
    fetchData();
  }, []);

  useEffect(() => {
    // Filter data based on the searchTerm
    const filtered = data.filter((item) =>
      item.Name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredData(filtered);
  }, [data, searchTerm]);

  const fetchData = () => {
    // Fetch data from the API endpoint
    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((jsonData) => {
        setData(jsonData); // Update the state with the fetched data
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };

  const handleDelete = (Id) => {
    const deleteApiUrl = `http://localhost:8080/delete-id/${Id}`;

    // Send a DELETE request to the delete endpoint
    fetch(deleteApiUrl, {
      method: 'DELETE',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        // Remove the deleted item from the data in state
        const updatedData = data.filter((item) => item.Id !== Id);
        setData(updatedData);
      })
      .catch((error) => {
        console.error('Error deleting data:', error);
      });
  };

  const handleEditClick = (Id) => {
    // Set the ID of the item being edited
    setEditingItemId(Id);
    // Find the contact data for editing
    const contactToEdit = data.find((item) => item.Id === Id);
    setEditContactData(contactToEdit);
  };

  const handleEditComplete = () => {
    // Clear the editing state
    setEditingItemId(null);
    setEditContactData(null); // Clear the edit contact data
  };

  return (
    <div>
      <h1>AddressBook</h1>
      <div className="container">
        <div className="search-container">
          <input
            className="search-bar"
            type="text"
            placeholder="Search by Name"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="create-container">
          <CreateContact />
        </div>
      </div>
      {searchTerm === '' ? (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Mobile No</th>
              <th>Email</th>
              <th>Address</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item) => (
              <tr key={item.Id}>
                <td>{item.Name}</td>
                <td>{item.Mobileno}</td>
                <td>{item.Email}</td>
                <td>{item.Address}</td>
                <td>
                  <button onClick={() => handleEditClick(item.Id)}>Edit</button>
                  {editingItemId !== null && (
        <EditContact
          contactId={item.Id}
          editContactData={item} // Pass the contact data for editing
          onEditComplete={handleEditComplete}
        />
      )}
                </td>
                <td>
                  <button onClick={() => handleDelete(item.Id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        filteredData.length > 0 && (
          <ul>
            {filteredData.map((item) => (
              <li key={item.Id}>
                <p>ID: {item.Id}</p>
                <p>Name: {item.Name}</p>
                <p>Email: {item.Email}</p>
                <p>Mobile No: {item.Mobileno}</p>
                <p>Address: {item.Address}</p>
                <button onClick={() => handleEditClick(item.Id)}>Edit</button>
                {editingItemId !== null && (
        <EditContact
          contactId={item.Id}
          editContactData={item} // Pass the contact data for editing
          onEditComplete={handleEditComplete}
        />
      )}
                <button onClick={() => handleDelete(item.Id)}>Delete</button>
              </li>
            ))}
          </ul>
        )
      )}
      
    </div>
  );
}

export default UpdateContact;
