import React, { useState } from 'react';

function CreateContactForm({ onClose, onSubmit }) {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [mobileNo, setMobileNo] = useState('');
    const [address, setAddress] = useState('');
    const [emailValid, setEmailValid] = useState(true); // Email validation flag
    const [phoneValid, setPhoneValid] = useState(true); // Phone number validation flag

  // ... (previous code)

  // Handle changes in the email input field
  const handleEmailChange = (e) => {
    const emailValue = e.target.value;
    setEmail(emailValue);

    // Use a regular expression to validate email format
    const emailPattern = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i;
    setEmailValid(emailPattern.test(emailValue));
  };

  // Handle changes in the phone number input field
  const handlePhoneChange = (e) => {
    const phoneValue = e.target.value;
    setPhone(phoneValue);
    const phonePattern = /^[0-9]{0,10}$/;
    setPhoneValid(phonePattern.test(phoneValue));
  };

    const handleSubmit = () => {
        // Perform form submission, e.g., send data to the server
        const newContact = {
            name,
            email,
            mobileNo,
            address,
        };
        onSubmit(newContact);
        onClose(); // Close the form modal
    };

    return (
        <div className="form-modal">
        <form>
            <div className="form-group">
                <label>Name:</label>
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} required/>
            </div>
            <div className="form-group">
                <label>Email:</label>
                <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} required/>
            </div>
            <div className="form-group">
                <label>Mobile No:</label>
                <input type="text" value={mobileNo} onChange={(e) => setMobileNo(e.target.value)} required/>
            </div>
            <div className="form-group">
                <label>Address:</label>
                <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} required/>
            </div>
            <div className="buttons">
                <button type="button" onClick={onClose}>Cancel</button>
                <button type="button" onClick={handleSubmit}>Create</button>
            </div>
        </form>
    </div>
    );
}

export default CreateContactForm;
