import React, { useState, useEffect } from 'react';

function EditContact({ contactId, onEditComplete }) {
  // Initialize state variables for form fields and form visibility
  const [name, setName] = useState('');
  const [mobileno, setMobileNo] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [isOpen, setIsOpen] = useState(true); // Initially open the form

  // Initialize state variables for validation
  const [nameValid, setNameValid] = useState(true);
  const [phoneValid, setPhoneValid] = useState(true);
  const [emailValid, setEmailValid] = useState(true);

  // Initialize error messages
  const [nameError, setNameError] = useState('');
  const [phoneError, setPhoneError] = useState('');
  const [emailError, setEmailError] = useState('');

  // API endpoint for editing a contact (replace with your actual endpoint)
  const editApiUrl = `http://localhost:8080/update/${contactId}`;

  // Fetch contact data and populate form fields when the component mounts
  useEffect(() => {
    fetch(editApiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        // Populate form fields with fetched data
        setName(data.Name);
        setMobileNo(data.Mobileno);
        setEmail(data.Email);
        setAddress(data.Address);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [editApiUrl]);

  // Handle changes in form fields
  const handleNameChange = (e) => {
    const nameValue = e.target.value;
    setName(nameValue);

    // Validate that name has at most 20 characters
    setNameValid(nameValue.length <= 20);

    // Display an error message if the name is invalid
    if (nameValue.length > 20) {
      setNameError('Name must have at most 20 characters.');
    } else {
      setNameError('');
    }
  };

  const handleMobileNoChange = (e) => {
    const phoneValue = e.target.value;
    setMobileNo(phoneValue);

    // Validate that phoneValue is a number and has at most 10 digits
    setPhoneValid(/^\d{0,10}$/.test(phoneValue));

    // Display an error message if the phone number is invalid
    if (!/^\d{0,10}$/.test(phoneValue)) {
      setPhoneError('Please enter a valid phone number (up to 10 digits).');
    } else {
      setPhoneError('');
    }
  };

  const handleEmailChange = (e) => {
    const emailValue = e.target.value;
    setEmail(emailValue);

    // Use a regular expression to validate email format
    setEmailValid(/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(emailValue));

    // Display an error message if the email is invalid
    if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(emailValue)) {
      setEmailError('Please enter a valid email address.');
    } else {
      setEmailError('');
    }
  };

  // Handle the "Save Changes" button click
  const handleEdit = () => {
    if (nameValid && phoneValid && emailValid) {
      const updatedContact = {
        Name: name,
        Mobileno: mobileno,
        Email: email,
        Address: address,
      };

      // Send a PUT request to update the contact
      fetch(editApiUrl, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedContact),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          console.log('Contact updated successfully');
          setIsOpen(false); // Close the form after successfully updating the contact
          onEditComplete(); // Notify the parent component of the edit completion
        })
        .catch((error) => {
          console.error('Error updating contact:', error);
        });
    } else {
      // Handle form submission errors (e.g., display error message)
    }
  };

  // Handle the "Cancel" button click to close the form without saving changes
  const handleCancel = () => {
    setIsOpen(false);
    onEditComplete(); // Notify the parent component of the edit completion
  };

  return (
    <div>
      {isOpen && (
        <form>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              value={name}
              onChange={handleNameChange}
              required
              className={nameValid ? '' : 'invalid'}
            />
            {!nameValid && (
              <div className="error-message">{nameError}</div>
            )}
          </div>
          <div className="form-group">
            <label>Phone Number:</label>
            <input
              type="text"
              value={mobileno}
              onChange={handleMobileNoChange}
              required
              className={phoneValid ? '' : 'invalid'}
            />
            {!phoneValid && (
              <div className="error-message">{phoneError}</div>
            )}
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              value={email}
              onChange={handleEmailChange}
              required
              className={emailValid ? '' : 'invalid'}
            />
            {!emailValid && (
              <div className="error-message">{emailError}</div>
            )}
          </div>
          <div className="form-group">
            <label>Address:</label>
            <textarea value={address} onChange={(e) => setAddress(e.target.value)} required />
          </div>
          <div className="form-group">
            <button type="button" onClick={handleEdit}>
              Save Changes
            </button>
            <button type="button" onClick={handleCancel}>
              Cancel
            </button>
          </div>
        </form>
      )}
    </div>
  );
}

export default EditContact;
