import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import CreateContactForm from './CreateContactForm';


function CreateContact() {
    const [data, setData] = useState(null);
    const [isFormVisible, setFormVisible] = useState(false);
    const [contacts, setContacts] = useState([]); // Store contacts data
    const apiUrl = 'http://localhost:8080/add-user/{$contacts.contact}'; // Replace with your API endpoint URL
    


    const openForm = () => {
        setFormVisible(true);
    };

    const closeForm = () => {
        setFormVisible(false);
    };

    const addContact = (newContact) => {
        // Add the new contact to the list of contacts
        setContacts([...contacts, newContact]);
    };




    useEffect(() => {
        fetch(apiUrl)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((jsonData) => {
                setData(jsonData);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, []);

    return (
        <div className="app">
            <button className='create-contact-button' onClick={openForm}> 
            <FontAwesomeIcon icon={faPlus} />
            
            Create Contact</button>

            {isFormVisible && (
                <CreateContactForm onClose={closeForm} onSubmit={addContact} />
            )}

        </div>
    );
    
}

export default CreateContact;
