import {Model} from 'react-model';
import React, { useState, useEffect } from 'react';

function EditContact({ contactId }) {
  // Initialize state variables for form fields and form visibility
  const [name, setName] = useState('');
  const [mobileno, setmobileno] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [isOpen, setIsOpen] = useState(true); // Initially open the form
  const[visible,setvisible]=useState(false);


  // API endpoint for editing a contact (replace with your actual endpoint)
  const editApiUrl = `http://localhost:8080/update/${contactId}`;

  // Fetch contact data and populate form fields when the component mounts
  useEffect(() => {
    fetch(editApiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        // Populate form fields with fetched data
        setName(data.name);
        setmobileno(data.mobileno);
        setEmail(data.email);
        setAddress(data.address);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [editApiUrl]); // Use the contactId in the URL to trigger the fetch when it changes

  // Handle changes in form fields
  const handleNameChange = (e) => setName(e.target.value);
  const handlemobilenoChange = (e) => setmobileno(e.target.value);
  const handleEmailChange = (e) => setEmail(e.target.value);
  const handleAddressChange = (e) => setAddress(e.target.value);

  // Handle the "Save Changes" button click
  const handleEdit = () => {
    setvisible(true);
    const updatedContact = {
      name,
      mobileno,
      email,
      address,
    };

    // Send a PUT request to update the contact
    fetch(editApiUrl, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedContact),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        console.log('Contact updated successfully');
        // Close the form after successfully updating the contact
        setIsOpen(false);
      })
      .catch((error) => {
        console.error('Error updating contact:', error);
      });
  };

  // Handle the "Cancel" button click to close the form without saving changes
  const handleCancel = () => {
    setIsOpen(false);
  };

  return (
    <div>
      {isOpen && (
        <Model isOpen={visible}>
        <form>
           <div className="form-group">
            <label>Name:</label>
            <input type="text" value={name} onChange={handleNameChange} required/>
          </div>
          <div className="form-group">
            <label>Phone Number:</label>
            <input type="text" value={mobileno} onChange={handlemobilenoChange} required/>
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input type="email" value={email} onChange={handleEmailChange} required/>
          </div>
          <div className="form-group">
            <label>Address:</label>
            <textarea value={address} onChange={handleAddressChange} required/>
          </div>
          <div className="form-group">
            <button type="button" onClick={handleEdit}>
              Save Changes
            </button>
            <button type="button" onClick={handleCancel}>
              Cancel
            </button>
          </div>
        </form>
        </Model>
      )}
    </div>
  );
}

export default EditContact;
