import React, { useState, useEffect } from 'react';

function UpdateContact() {
    const [contacts, setContacts] = useState([]);
    const [selectedContact, setSelectedContact] = useState(null);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        // Fetch data from the API endpoint
        fetch("http://localhost:8080/")
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((jsonData) => {
                setData(jsonData); // Update the state with the fetched data
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };

    return (
        <div>
            <h1>Data from API</h1>
            {data.length > 0 ? (
                <ul>
                    {data.map((item) => (
                        <li key={item.Id}>
                            <p>ID: {item.Id}</p>
                            <p>Name: {item.Name}</p>
                            <p>Mobile No: {item.Mobileno}</p>
                            <p>Email: {item.Email}</p>
                            <p>Address: {item.Address}</p>
                        </li>
                    ))}
                </ul>
            ) : null 
            }
        </div>
    );

    const handleContactClick = (name) => {
        // Construct the API endpoint URL using the contactId
        const apiUrl = `http://your-api-url/api/another-endpoint/${name}`; // Replace with the new API endpoint URL

        // Fetch data using the constructed URL
        fetch(apiUrl)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((jsonData) => {
                // Set the selected contact data in state
                setSelectedContact(jsonData);
            })
            .catch((error) => {
                console.error('Error fetching contact data:', error);
            });
    };

    return (
        <div>
            <h1>Contact List</h1>
            <ul>
                {contacts.map((contact) => (
                    <li key={contact.Id} onClick={() => handleContactClick(contact.name)}>
                        {contact.Name}
                    </li>
                ))}
            </ul>

            {selectedContact && (
                <div>
                    <h2>Contact Details</h2>
                    <p>ID: {selectedContact.Id}</p>
                    <p>Name: {selectedContact.Name}</p>
                    <p>Email: {selectedContact.Email}</p>
                    <p>Mobile No: {selectedContact.Mobileno}</p>
                    <p>Address: {selectedContact.Address}</p>
                </div>
            )}
        </div>
    );
}

export default UpdateContact;
