import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import CreateContactForm from './CreateContactForm';


function CreateContact() {
    const [data, setData] = useState('');
    const [isFormOpen, setFormOpen] = useState(false);
    const [contacts, setContacts] = useState([]); // Store contacts data
    const [name, setName] = useState('');
    const[phoneno,setPhone]=useState('');
    const [email, setEmail] = useState('');
    const [address, setAddress] = useState('');

    const apiUrl = 'http://localhost:8080/add-user'; // Replace with your API endpoint URL
    


    const openForm = () => {
        setFormOpen(true);
    };

    const closeForm = () => {
        setFormOpen(false);
    };

    const addContact = (newContact) => {
        setContacts([...contacts, newContact]);
        closeForm(); // Close the form modal
    };

    const handleSubmit = () => {
        const newContact = {
            name,
            phoneno,
            email,
            address
        };
        fetch(apiUrl, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newContact)
        }).then((response) => {
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            return response.text(); // Read the response as text
          })
        .then((response) => response.json())
        .then((newContact) => {
            console.log(newContact);
            // Clear form fields
            setName('');
            setPhone('');
            setEmail('');
            setAddress('');
        })
        .catch((error) => {
            console.error('Error:', error);
        });
    };


    useEffect(() => {
        fetch(apiUrl)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((jsonData) => {
                setData(jsonData);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, []);

    return (
        <div className="app">
            <button className='create-contact-button' onClick={openForm}> 
            <FontAwesomeIcon icon={faPlus} />
            Create Contact</button>
            <CreateContactForm isOpen={isFormOpen} onRequestClose={closeForm} onSubmit={addContact} />
            { <ul>
                {contacts.map((contact, index) => (
                    <li key={index}>
                        <p>Name: {contact.name}</p>
                        <p>Email: {contact.email}</p>
                        <p>Mobile No: {contact.mobileNo}</p>
                        <p>Address: {contact.address}</p>
                    </li>
                ))}
            </ul> }

        </div>
    );
    
}

export default CreateContact;
