import React, { useState, useEffect } from 'react';

function EditContact({ contactId, onEditComplete }) {
  // Initialize state variables for form fields and form visibility
  const [name, setName] = useState('');
  const [mobileno, setMobileNo] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [isOpen, setIsOpen] = useState(true); // Initially open the form
  const [nameValid, setNameValid] = useState(true); // Name validation flag
  const [phoneValid, setPhoneValid] = useState(true); // Phone number validation flag
  const [emailValid, setEmailValid] = useState(true); // Email validation flag

  // API endpoint for editing a contact (replace with your actual endpoint)
  const editApiUrl = `http://localhost:8080/update/${contactId}`;

  // Fetch contact data and populate form fields when the component mounts
  useEffect(() => {
    fetch(editApiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        // Populate form fields with fetched data
        setName(data.name);
        setMobileNo(data.mobileno);
        setEmail(data.email);
        setAddress(data.address);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [editApiUrl]);

  // ... (previous code)

  // Handle the "Save Changes" button click
  const handleEdit = () => {
    if (nameValid && phoneValid && emailValid) {
      const updatedContact = {
        name,
        mobileno,
        email,
        address,
      };

      // Send a PUT request to update the contact
      fetch(editApiUrl, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedContact),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          console.log('Contact updated successfully');
          setIsOpen(false); // Close the form after successfully updating the contact
          onEditComplete(); // Notify the parent component that the edit is complete
        })
        .catch((error) => {
          console.error('Error updating contact:', error);
        });
    } else {
      // Handle form submission errors (e.g., display error message)
    }
  };

  // ... (previous code)

  return (
    <div>
      {isOpen && (
        <form>
          {/* ... (previous form elements) */}
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              value={name}
              onChange={handleNameChange}
              required
              className={nameValid ? '' : 'invalid'}
            />
            {!nameValid && (
              <div className="error-message">Name must have at most 20 characters.</div>
            )}
          </div>
          <div className="form-group">
            <label>Phone Number:</label>
            <input
              type="text"
              value={mobileno}
              onChange={handleMobileNoChange}
              required
              className={phoneValid ? '' : 'invalid'}
            />
            {!phoneValid && (
              <div className="error-message">Please enter a valid phone number (up to 10 digits).</div>
            )}
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              value={email}
              onChange={handleEmailChange}
              required
              className={emailValid ? '' : 'invalid'}
            />
            {!emailValid && (
              <div className="error-message">Please enter a valid email address.</div>
            )}
          </div>
          {/* ... (previous form elements) */}
        </form>
      )}
    </div>
  );
}

export default EditContact;
